package guideSeries1;

public class StringObjectImmutable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String str="Java";
		String Str="Java";
		if (str==Str) {
			System.out.println("Same String here");
		}
		else {
			System.out.println("Not Same String");
		}
	}

}
